    "neuron <- function(x1, x2, target, w, a, fnet, theta, epochs){\n",
    "  # x1     : data untuk x1\n",
    "  # x2     : data untuk x2\n",
    "  # target : data hasil x1 dan x2\n",
    "  # w      : bobot awal\n",
    "  # a      : learning rate\n",
    "  # net    : hasil penjumlahan bobot x input\n",
    "  # fnet   : fungsi aktivasi\n",
    "  # y      : hasil fungsi aktivasi\n",
    "  # theta  : nilai threshold\n",
    "  # epochs : banyak nya iterasi\n",
    "  \n",
    "  for(epoch in 1:epochs){\n",
    "    y_temp <- c()\n",
    "    \n",
    "    cat('epoch ke :', epoch,'\\n')\n",
    "    cat('  input           NET   f(NET)     t        D_w              w \\n')\n",
    "    \n",
    "    for(i in 1:length(target)){\n",
    "      input <- c(x1[i], x2[i], 1)\n",
    "      net <- sum(input * w)\n",
    "      y <- fnet(net, theta)\n",
    "      t <- target[i]\n",
    "      delta_w <- a*t*input\n",
    "      if(y == t) delta_w <- c(0,0,0)\n",
    "      w <- w + delta_w\n",
    "      \n",
    "      y_temp[i] <- y\n",
    "      \n",
    "      cat(format(input, width = 2), '\\t',\n",
    "          format(net, width =3), '\\t',\n",
    "          format(y, width = 3), '\\t',\n",
    "          format(t, width = 3), '\\t',\n",
    "          format(delta_w, width = 2),'\\t',\n",
    "          format(w, width = 2),\n",
    "          '\\n')\n",
    "    }\n",
    "      cat('\\n')\n",
    "      if(all(y_temp == target)) break\n",
    "  }\n",
    "    return(w)\n",
    "}"
   ]
  },
