    "view_plot <- function(x1, x2, t, w, theta, title=\"plot\"){\n",
    "    xlim <- c(min(x1)-0.5, max(x1)+0.5)\n",
    "    ylim <- c(min(x2)-0.5, max(x2)+0.5)\n",
    "    plot(x1, x2, col = \"blue\", pch = 16, cex=2, xlim=xlim, ylim=ylim)\n",
    "    abline(h = 0, col = \"gray\", lty = 2)\n",
    "    abline(v = 0, col = \"gray\", lty = 2)\n",
    "\n",
    "    for(i in 1:length(x1)){\n",
    "        if( t[i] == 1 )points(x1[i], x2[i], col=\"red\", pch = 16, cex=2)\n",
    "    }\n",
    "    \n",
    "    x <- seq(min(x1)-1, max(x1)+1, by=0.01)\n",
    "    y <- -w[1]*x/w[2] - w[3]/w[2]\n",
    "    lines(x, y, lwd = 2)\n",
    "    lines(x, y+theta, col = \"grey\", lty = 2, lwd = 1)\n",
    "    lines(x, y-theta, col = \"grey\", lty = 2, lwd = 1)\n",
    "    \n",
    "    title(main = title)\n",
    "}"
   ]
  },
